import { useEffect, useState } from 'react';

export default function ProjectCard({Title, Description, Class, Company}){
    const [isMobile, setIsMobile] = useState(false);
    
    useEffect(() => {
        const checkMobile = () => {
            setIsMobile(window.innerWidth <= 768);
        };
        
        // Initial check
        checkMobile();
        
        // Add event listener
        window.addEventListener('resize', checkMobile);
        
        // Cleanup
        return () => window.removeEventListener('resize', checkMobile);
    }, []);

    return(
        <>
        <div className="cards">
            <div className={Class} style={{
                width: isMobile ? '90%' : '650px', 
                height: 'auto',
                minHeight: isMobile ? '220px' : '200px',
                fontSize: isMobile ? '0.9rem' : '1rem'
            }}>
              <p className="card__exit"><i className="fas fa-times"></i></p>
              <h2 className="card__title" style={{marginTop: -20}}>
                {Company}
              </h2>
              <h5 style={{color: "white", marginTop: -20, fontSize: isMobile ? '0.9rem' : '1.1rem'}}>{Title}</h5>
                <div style={{marginTop: -30}}>{Description}</div>  
            </div>
        </div>
        </>
      )
}
