import './About.css'
import Button from 'react-bootstrap/Button';
import { useEffect, useState } from 'react';

export default function AboutPage(){
    const [isMobile, setIsMobile] = useState(false);
    
    useEffect(() => {
        const checkMobile = () => {
            setIsMobile(window.innerWidth <= 768);
        };
        
        // Initial check
        checkMobile();
        
        // Add event listener
        window.addEventListener('resize', checkMobile);
        
        // Cleanup
        return () => window.removeEventListener('resize', checkMobile);
    }, []);

    return ( 
        <>
        <div className="wrapper" >
	        <div className="box" >
                <p>Hey, I'm Joseph, a 4th year student, studying computer science and business at the University of British Columbia. I 
                love the interplay of technology and people and am always excited to creatively solve real problems with 
                software. 
                </p>
            </div>
        </div>
        <h1 style={{marginTop: -30, fontSize: isMobile ? '2rem' : '2.5rem'}}>Languages</h1>
        <div className="skills-container">
            <Button className='buttonClass' variant="outline-light" size={isMobile ? "sm" : "md"}>C#</Button>{' '}
            <Button className='buttonClass' variant="outline-light" size={isMobile ? "sm" : "md"}>C/C++</Button>{' '}
            <Button className='buttonClass' variant="outline-light" size={isMobile ? "sm" : "md"}>Java</Button>{' '}
            <Button className='buttonClass' variant="outline-light" size={isMobile ? "sm" : "md"}>JavaScript/TypeScript</Button>{' '}
            <Button className='buttonClass' variant="outline-light" size={isMobile ? "sm" : "md"}>Python</Button>{' '}
            <Button className='buttonClass' variant="outline-light" size={isMobile ? "sm" : "md"}>HTML/CSS/Razor</Button>{' '}
            <Button className='buttonClass' variant="outline-light" size={isMobile ? "sm" : "md"}>SQL/LINQ</Button>{' '}
        </div>
        <h1 style={{color: 'black', fontSize: isMobile ? '2rem' : '2.5rem'}}>Programs/Frameworks</h1>
        <div className="skills-container">
            <Button className='buttonClass' variant="outline-light" size={isMobile ? "sm" : "md"}>ASP.NET CORE</Button>{' '}
            <Button className='buttonClass' variant="outline-light" size={isMobile ? "sm" : "md"}>React.js</Button>{' '}
            <Button className='buttonClass' variant="outline-light" size={isMobile ? "sm" : "md"}>Excel/VBA</Button>{' '}
            <Button className='buttonClass' variant="outline-light" size={isMobile ? "sm" : "md"}>Tableau</Button>{' '}
            <Button className='buttonClass' variant="outline-light" size={isMobile ? "sm" : "md"}>Power BI</Button>{' '}
            <Button className='buttonClass' variant="outline-light" size={isMobile ? "sm" : "md"}>DevExpress</Button>{' '}
        </div>
    </>)
}
