import { useEffect, useState } from 'react';

export default function ProjectCard({Title, Description, Class, Company}){
    const [isMobile, setIsMobile] = useState(false);
    const [isLargeScreen, setIsLargeScreen] = useState(false);
    
    useEffect(() => {
        const checkScreenSize = () => {
            setIsMobile(window.innerWidth <= 768);
            setIsLargeScreen(window.innerWidth >= 1600);
        };
        
        // Initial check
        checkScreenSize();
        
        // Add event listener
        window.addEventListener('resize', checkScreenSize);
        
        // Cleanup
        return () => window.removeEventListener('resize', checkScreenSize);
    }, []);

    return(
        <>
        <div className="cards">
            <div className={Class} style={{
                width: isMobile ? '90%' : (isLargeScreen ? '800px' : '650px'), 
                height: 'auto',
                minHeight: isMobile ? '220px' : (isLargeScreen ? '280px' : '200px'),
                fontSize: isMobile ? '0.9rem' : (isLargeScreen ? '1.3rem' : '1rem'),
                padding: isLargeScreen ? '35px' : undefined
            }}>
              <p className="card__exit"><i className="fas fa-times"></i></p>
              <h2 className="card__title" style={{
                marginTop: -20,
                fontSize: isLargeScreen ? '2.3rem' : undefined
              }}>
                {Company}
              </h2>
              <h5 style={{
                color: "white", 
                marginTop: -20, 
                fontSize: isMobile ? '0.9rem' : (isLargeScreen ? '1.6rem' : '1.1rem')
              }}>
                {Title}
              </h5>
              <div style={{
                marginTop: -30,
                fontSize: isLargeScreen ? '1.2rem' : undefined
              }}>
                {Description}
              </div>  
            </div>
        </div>
        </>
      )
}
